// import logo from './logo.svg';
import React, { useState } from "react";
import "./App.css";
import Login from "./component/login";
import Register from "./component/register";

export default function App() {
  const [token, setToken] = useState(null);
  const [isLogin, setIsLogin] = useState(true);
  // Login method
  e = login = (username, password) => {
    const apiUrl = "http://127.0.0.1:8000/auth/";
    let credentials = {
      username: username,
      password: password,
    };

    fetch(apiUrl, {
      method: "POST",
      headers: { "Content-type": "application/json" },
      body: JSON.stringify(this.credentials),
    })
      .then((data) => data.json())
      .then((data) => {
        this.props.userLogin(data.token);
      })
      .catch((error) => console.error(error));
  };

  // Register method
  e = register = (username, password, firstname, lastname) => {
    const apiUrl = "http://127.0.0.1:8000/api/users/";
    let credentials = {
      username: username,
      password: password,
      first_name: firstname,
      last_name: lastname,
    };

    fetch(apiUrl, {
      method: "POST",
      headers: { "Content-type": "application/json" },
      body: JSON.stringify(this.credentials),
    })
      .then((data) => data.json())
      .then((data) => {
        this.props.userLogin(data.token);
      })
      .catch((error) => console.error(error));
  };

  return (
    <div className="App">
      <Login login={login} setIsLogin={setIsLogin}/>
      <Register register={register} setIsLogin={setIsLogin}/>
    </div>
  );
}
