import { useState } from "react";
import { Button } from 'react-bootstrap';

export default function Login({ login, setIsLogin }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = () => {
    login(username, password);
  };

  return (
    <div>
      <label>
          User
      </label>
      <input value={username} onChange={(e) => setUsername(e.target.value)} /><br/>
      <input value={password} type="password" onChange={(e) => setPassword(e.target.value)} /><br/>
      <Button variant="primary" onClick={() => handleLogin()}>login</Button>{' '}
      <Button variant="Secondary" onClick={() => setIsLogin(false)}>register</Button>
    </div>
  );
}
